import React, { useEffect, useState } from "react"
import api from '../api'
import _ from 'lodash'

interface recipe {
  id: number,
  type: string,
  recipe: string,
  title: string,
  most_similar: [number],
  ratings: number,
  stars: number,
  image: string,
  ingredients: string,
  directions: string
}

const HomePage: React.FC = () => {
  const [temp, setTemp] = useState("");
  const [recipes, setRecipes] = useState<recipe[]>();
  const [mainRecipes, setMainRecipes] = useState<recipe[]>();

  useEffect(() => {
    api.get('getdata/dinner_recipes').then((data) => {
      const json = JSON.parse(data.data.fileData);
      setRecipes(json);
      let temp = _.filter(json, (recipe: recipe) => {
          console.log(recipe.type)
          return recipe.type === "main";
      });
      setMainRecipes(temp);

      console.log(mainRecipes);
  })}, []);

  useEffect(() => {    
    setTemp("test");
  }, [recipes])

  // useEffect(() => { if (recipes) parseData(recipes) }, [recipes]);

  return mainRecipes ? (
    <div>
      <div id="recipe1">
        <span>1. { mainRecipes[0] }</span>
      </div>
      <div id="recipe2">
        <span>2. { mainRecipes[1] }</span>
      </div>
      <div id="recipe3">
        <span>3. { mainRecipes[2] }</span>
      </div>
      <div id="recipe4">
        <span>4. { mainRecipes[3] }</span>
      </div>
      <div id="recipe5">
        <span>5. { mainRecipes[4] }</span>
      </div>
    </div>    
  )
  : (<div>No Recipes Found</div>)
}

export default HomePage
