import React from "react"
import {Card, CardHeader, CardTitle, CardBody, Col, CardImg} from "reactstrap"
import StarRatingComponent from 'react-star-rating-component';

interface RecipeCompProps {
    recipe: any;
}

export const RecipeComp: React.FC<RecipeCompProps> = (props) => {
    return props.recipe ? (
        <div>
            <Card className="card-chart">
                <CardHeader>
                    <CardTitle tag="h3">
                        <i className="tim-icons icon-bell-55 text-info" />{" "}
                        {props.recipe.title}
                    </CardTitle>
                </CardHeader>
                <CardImg variant="top" src={props.recipe.ratings}/>
                <CardBody>
                    <Col>
                        {/* <img src={props.recipe.image}></img> */}
                        <StarRatingComponent 
                            name="rating"
                            value={props.recipe.ratings}
                            editing={false}
                        />
                        <Col>
                            <Card>
                                <CardHeader className="sub-header">
                                    <i className="tim-icons icon-bell-55 text-info" />{" "}
                                    Ingredients
                                </CardHeader>
                                <CardBody>
                                    {props.recipe.ingredients}
                                    {/* <ul className="list-group">
                                        {props.recipe.ingredients.map((item, key) => (
                                            <li className="list-group-item">
                                                {listitem}
                                            </li>
                                        ))}
                                    </ul> */}
                                </CardBody>
                            </Card>
                        </Col>
                        <Col>
                            <Card>
                                <CardHeader className="sub-header">
                                    <i className="tim-icons icon-bell-55 text-info" />{" "}
                                    Direction
                                </CardHeader>
                                <CardBody>
                                    {props.recipe.directions}
                                </CardBody>
                            </Card>                        
                        </Col>
                    </Col>
                    <div>

                    </div>
                </CardBody>
            </Card>
        </div>
    ) : (<div></div>);   
}
